SELECT ttbooks.course_code saw_0, ttbooks.course_name saw_1, ttbooks.title_author saw_2, ttbooks.title saw_3, ttbooks.mms_id saw_4, ttbooks.call_no saw_5 FROM (SELECT CASE WHEN creserves2.course_code IS NULL THEN 'TT-OTHER' ELSE creserves2.course_code END course_code, CASE WHEN creserves2.course_name IS NULL THEN 'Top Textbooks - Other' ELSE creserves2.course_name END course_name, creserves.title_author title_author, creserves.title title, creserves.mms_id mms_id, CASE WHEN call_nos.call_no IS NULL THEN 'Ebook - Check catalog' ELSE call_nos.call_no END call_no FROM (SELECT "Bibliographic Details"."Title Author Combined and Normalized" title_author, "Bibliographic Details"."Title" title, "Bibliographic Details"."MMS Id" mms_id FROM "Course Reserves" WHERE ("Reading List"."Reading List Code" IN ('Top Textbooks - Spring 2023')) ) creserves LEFT JOIN (SELECT "Course"."Course Code" course_code, "Course"."Course Name" course_name, "Bibliographic Details"."MMS Id" mms_id FROM "Course Reserves" WHERE ("Reading List"."Reading List Name" NOT LIKE 'Top Textbooks%') AND ("Course"."Course Code" LIKE '%Spring 2023%')) creserves2 ON creserves.mms_id = creserves2.mms_id INNER JOIN (SELECT "Title Details"."Permanent Call Number" call_no, "Bibliographic Details"."MMS Id" mms_id FROM "Titles") call_nos ON creserves.mms_id = call_nos.mms_id) ttbooks 