SELECT pol_funds.pol_ref, pol_funds.amount, pol_funds.fund_name, pol_funds.fund_code, pol_funds.invoice_line_id, pol_funds.invoice_number, pol_funds.invoice_line_number, pol_funds.transaction_type, pol_funds.num_transactions FROM (SELECT "PO Line"."PO Line Reference" pol_ref, "Fund Transactions"."Transaction Amount" amount, "Fund Ledger"."Fund Ledger Name" fund_name, "Fund Ledger"."Fund Ledger Code" fund_code, "Invoice Line"."Invoice Line-Unique Identifier" invoice_line_id, "Invoice Line"."Invoice-Number" invoice_number, "Invoice Line"."Invoice Line-Number" invoice_line_number, "Fund Transaction Details"."Transaction Item Sub Type" transaction_type, COUNT(DISTINCT "Fund Transaction Details"."Transaction Id") num_transactions FROM "Funds Expenditure" WHERE ("Fund Ledger"."Fund Type" = 'Allocated fund') AND ("Fiscal Period"."Fiscal Period Filter" = 'Current Fiscal Year') GROUP BY "PO Line"."PO Line Reference", "Fund Transactions"."Transaction Amount", "Fund Ledger"."Fund Ledger Name", "Fund Ledger"."Fund Ledger Code", "Invoice Line"."Invoice Line-Unique Identifier", "Invoice Line"."Invoice-Number", "Invoice Line"."Invoice Line-Number", "Fund Transaction Details"."Transaction Item Sub Type") pol_funds WHERE pol_funds.num_transactions > 1