SELECT encumbered_pols.pol_reference saw_0, encumbered_pols.pol_title saw_1, encumbered_pols.encumbered_amount saw_2, encumbered_pols.mms_id saw_3, other_pols.pol_reference saw_4, other_pols.last_invoice_date saw_5, other_pols.pol_status FROM (SELECT "Funds Expenditure"."PO Line"."PO Line Reference" pol_reference, "Funds Expenditure"."PO Line"."PO Line Title" pol_title, "Funds Expenditure"."Fund Transactions"."Transaction Encumbrance Amount" encumbered_amount, "Bibliographic Details"."MMS Id" mms_id FROM "Funds Expenditure" WHERE (("Fiscal Period"."Fiscal Period Filter" = 'Current Fiscal Year') AND ("Fund Transactions"."Transaction Encumbrance Amount" > 0) AND ("PO Line Type"."Continuity" = 'CONTINUOUS'))) encumbered_pols LEFT JOIN (SELECT "Funds Expenditure"."PO Line"."PO Line Reference" pol_reference, "Bibliographic Details"."MMS Id" mms_id, MAX("Invoice Line"."Invoice-Creation Date") last_invoice_date, "PO Line"."Status" pol_status FROM "Funds Expenditure") other_pols ON (encumbered_pols.mms_id = other_pols.mms_id) WHERE saw_1 <> saw_4