SELECT all_count.pol_all saw_0, all_count.fund_name saw_1, encumbered_count.pol_one_dollar saw_2 FROM (SELECT COUNT(all_pols.po_line_reference) pol_all, all_pols.fund_name fund_name FROM (SELECT "PO Line"."PO Line Reference" po_line_reference, "Fund Ledger"."Fund Ledger Name" fund_name FROM "Funds Expenditure" WHERE ("PO Line Type"."Continuity" = 'CONTINUOUS') AND ("Fiscal Period"."Fiscal Period Filter" = 'Current Fiscal Year') AND (("Fund Transactions"."Transaction Encumbrance Amount" > 0) OR ("Fund Transactions"."Transaction Expenditure Amount" > 0)) AND ("Fund Ledger"."Fund Type" = 'Allocated fund')) all_pols GROUP BY all_pols.fund_name) all_count INNER JOIN (SELECT COUNT(encumbered.po_line_reference) pol_one_dollar, encumbered.fund_name fund_name FROM (SELECT "PO Line"."PO Line Reference" po_line_reference, "Fund Ledger"."Fund Ledger Name" fund_name, "Fund Ledger"."Fund Type" saw_2, "Fund Transactions"."Transaction Encumbrance Amount" saw_3 FROM "Funds Expenditure" WHERE ("PO Line Type"."Continuity" = 'CONTINUOUS') AND ("Fund Transactions"."Transaction Encumbrance Amount" = 1) AND ("Fund Ledger"."Fund Type" = 'Allocated fund')) encumbered GROUP BY encumbered.fund_name) encumbered_count ON all_count.fund_name = encumbered_count.fund_name