SELECT users.user_group saw_0, users.num_users saw_1, loans.num_users_with_loans saw_3, loans.num_loans saw_2 FROM (SELECT "User Details"."User Group" user_group, COUNT(DISTINCT "User Details"."Primary Identifier") num_users FROM "Users" WHERE ("User Details"."Primary Identifier" LIKE 'GWG%')) users LEFT JOIN (SELECT COUNT(DISTINCT "Loan Details"."Item Loan Id") num_loans, COUNT(DISTINCT "Borrower Details"."Primary Identifier") num_users_with_loans, "Borrower Details"."User Group" user_group FROM "Fulfillment" WHERE (("Borrower Details"."Primary Identifier" LIKE 'GWG%') AND ("Loan Details"."Loan Status" = 'Active'))) loans ON users.user_group = loans.user_group