SELECT "Fund Transactions"."Transaction Expenditure Amount" expenditure, "Transaction Date"."Transaction Date" trans_date, "Fiscal Period"."Fiscal Period Description" fiscal_desc, "Fund Ledger"."Fund Ledger Name" fund_name, SUBSTRING("Fund Ledger"."Fund Ledger Code" FROM 1 FOR 6) ledger_name, "Fund Ledger"."Parent Fund Ledger Name" parent_fund, "Vendor"."Vendor Code" vendor_code, "PO Line"."Material Type" material_type, "PO Line"."PO Line Reference" pol_ref, "PO Line"."Status" pol_status, "PO Line Type"."Acquisition Material Type Description" pol_acq_type, "PO Line Type"."PO Line Type Name" pol_type, "Bibliographic Details"."Title" title, "Invoice Line"."Invoice Line-Unique Identifier" inv_line, "PO Line Type"."Continuity" continuity FROM "Funds Expenditure" WHERE ("Fund Ledger"."Fund Type" = 'Allocated fund') AND ("PO Line"."Status" IN ('CANCELLED', 'CLOSED')) AND )