SELECT phys_holdings.mms_id, phys_holdings.material_type, phys_holdings.title, phys_holdings.num_items FROM (SELECT "Bibliographic Details"."MMS Id" mms_id, "Bibliographic Details"."Material Type" material_type, "Bibliographic Details"."Title" title, "Physical Item Details"."Num of Items (In Repository)" num_items FROM "Physical Items" WHERE ("Bibliographic Details"."Material Type" IN ('Mixed material', 'Visual material')) AND ("Physical Item Details"."Lifecycle" = 'Active') AND ("Bibliographic Details"."Suppressed From Discovery" = 'No') AND ("Holding Details"."Suppressed from Discovery" = 0) AND ("Bibliographic Details"."Bibliographic Lifecycle" = 'In Repository') AND ("Temporary Location"."Temporary Location Code" = 'wrlc stru')) phys_holdings LEFT JOIN (SELECT "Bibliographic Details"."MMS Id" mms_id, "Portfolio"."No. of Portfolio (In Repository)" num_portfolios FROM "E-Inventory") portfolios ON portfolios.mms_id = phys_holdings.mms_id WHERE portfolios.mms_id IS NULL