SELECT portfolios.mms_id, portfolios.material_type, portfolios.title, portfolios.num_portfolios FROM (SELECT "Bibliographic Details"."MMS Id" mms_id, "Bibliographic Details"."Material Type" material_type, "Bibliographic Details"."Title" title, "Portfolio"."No. of Portfolio (In Repository)" num_portfolios FROM "E-Inventory" WHERE ("Bibliographic Details"."Material Type" IN ('Mixed material', 'Visual material')) AND ("Portfolio"."Availability" = 'Available') AND ("Portfolio"."Lifecycle" = 'In Repository') AND ("Bibliographic Details"."Suppressed From Discovery" = 'No') AND ("Bibliographic Details"."Bibliographic Lifecycle" = 'In Repository')) portfolios INNER JOIN (SELECT "Bibliographic Details"."MMS Id" mms_id FROM "Physical Items" WHERE ("Holding Details"."Suppressed from Discovery" = 0) AND ("Physical Item Details"."Lifecycle" = 'Active')) phys_holdings ON portfolios.mms_id = phys_holdings.mms_id