SELECT SUM(loans.num_loans), COUNT(DISTINCT loans.borrower_id), COUNT(DISTINCT users.user_id), users.user_group FROM (SELECT "Loan"."Loans (In House + Not In House)" num_loans, "Borrower Details"."User Id" borrower_id, "Borrower Details"."User Group" user_group FROM "Fulfillment" WHERE "Loan Details"."Loan Status" = 'Active') loans RIGHT JOIN (SELECT "User Details"."User Id" user_id, "User Details"."User Group" user_group FROM "Users") users ON loans.borrower_id = users.user_id GROUP BY users.user_group