SELECT pol_funds.pol_ref, pol_funds.po_title, pol_funds.renewal_date, pol_funds.fund_name, pol_funds.fund_code, pol_funds.invoice_status, pol_funds.encumbrance, pol_funds.expenditure FROM (SELECT "PO Line"."PO Line Reference" pol_ref, "PO Line"."PO Line Title" po_title, "PO Line"."Renewal Date" renewal_date, "Fund Ledger"."Fund Ledger Name" fund_name, "Fund Ledger"."Fund Ledger Code" fund_code, "Invoice Line"."Invoice-Status" invoice_status, "Fund Transactions"."Transaction Encumbrance Amount" encumbrance, "Fund Transactions"."Transaction Expenditure Amount" expenditure FROM "Funds Expenditure" WHERE ("Fund Ledger"."Fund Type" = 'Allocated fund') AND ("Fiscal Period"."Fiscal Period Filter" = 'Current Fiscal Year') AND ("PO Line Type"."Continuity" = 'CONTINUOUS')) pol_funds INNER JOIN (SELECT "PO Line"."PO Line Reference" pol_ref, COUNT(DISTINCT "Fund Ledger"."Fund Ledger Code") num_funds FROM "Funds Expenditure" WHERE ("Fund Ledger"."Fund Type" = 'Allocated fund') AND ("Fiscal Period"."Fiscal Period Filter" = 'Current Fiscal Year') GROUP BY "PO Line"."PO Line Reference") funds_by_pol ON pol_funds.pol_ref = funds_by_pol.pol_ref AND funds_by_pol.num_funds > 1