SELECT items.item_id, items.mms_id, reading_lists.rlist_code FROM (SELECT "Physical Item Details"."Item Id" item_id, "Bibliographic Details"."MMS Id" mms_id FROM "Physical Items") AS items INNER JOIN (SELECT "Reading List"."Reading List Code" rlist_code, "Bibliographic Details"."MMS Id" mms_id FROM "Course Reserves" WHERE "Reading List"."Reading List Code" IN ('Top Textbooks - Fall 2018', 'Top Textbooks - Spring 2019')) AS reading_lists ON items.mms_id = reading_lists.mms_id