SELECT SUM(loans.num_loans) saw_0, loans.title saw_1 FROM (SELECT "Loan"."Loans (Not In House)" num_loans, "Physical Item Details"."Item Id" item_id, "Bibliographic Details"."MMS Id" mms_id, "Bibliographic Details"."Title" title FROM "Fulfillment" WHERE ("Loan Date"."Loan Date" >= date '2019-08-25') AND ("Loan Policy"."Policy Name" = '3 hr loan')) loans INNER JOIN (SELECT "Physical Item Details"."Item Id" item_id, "Holding Details"."Holding Id" holding_id, "Bibliographic Details"."MMS Id" mms_id, "Temporary Location"."Temporary Location Name" temp_loc, "Physical Item Details"."Barcode" barcode FROM "Physical Items" WHERE "Temporary Location"."Temporary Location Name" IN ('Reserves (3 hours)', 'Eckles Reserves')) items ON loans.item_id = items.item_id INNER JOIN (SELECT "Reading List"."Reading List Name" rlist, "Reading List"."Reading List Id" rlist_id, "Bibliographic Details"."MMS Id" mms_id, "Bibliographic Details"."Title" title FROM "Course Reserves" WHERE "Reading List"."Reading List Code" = 'Top Textbooks - Fall 2019') titles ON items.mms_id = titles.mms_id GROUP BY loans.title