SELECT MAX(orders.list_price) saw_0, SUM(orders.quantity) saw_1, SUM(orders.net_price) saw_2, orders.title saw_3 FROM (SELECT "PO Line"."PO Line Reference" pol_ref, "PO Line"."Net Price" net_price, "PO Line"."List Price" list_price, "PO Line"."Quantity for Pricing" quantity, "Bibliographic Details"."Title" title, "Bibliographic Details"."MMS Id" mms_id FROM "Funds Expenditure" WHERE "PO Line"."Status" <> 'CANCELLED' AND "PO Line Type"."Format" = 'P') orders INNER JOIN (SELECT "Reading List"."Reading List Id" rlist_id, "Bibliographic Details"."MMS Id" mms_id FROM "Course Reserves" WHERE "Reading List"."Reading List Code" = 'Top Textbooks - Fall 2019') titles ON orders.mms_id = titles.mms_id GROUP BY orders.title