SELECT creserves.course_code saw_0, creserves.course_name saw_1, ttbooks.title_author saw_2, ttbooks.title saw_3, ttbooks.mms_id saw4 FROM (SELECT "Course"."Course Code" course_code, "Course"."Course Name" course_name, "Course"."Course Year" course_year, "Course"."Course Term" course_term, "Reading List"."Reading List Name" rlist_name, "Bibliographic Details"."Title Author Combined and Normalized" title_author, "Bibliographic Details"."MMS Id" mms_id FROM "Course Reserves" WHERE ("Reading List"."Reading List Name" NOT LIKE 'Top Textbooks%') AND ("Course"."Course Status" = 'Active')) creserves INNER JOIN (SELECT "Reading List"."Reading List Code" tt_list, "Bibliographic Details"."Title" title, "Bibliographic Details"."Title Author Combined and Normalized" title_author, "Bibliographic Details"."MMS Id" mms_id FROM "Course Reserves" WHERE "Reading List"."Reading List Code" IN ('Top Textbooks - Spring 2022')) ttbooks ON creserves.mms_id = ttbooks.mms_id