SELECT creserves.course_code saw_0, creserves.course_name saw_1, creserves.course_year saw_2, creserves.course_term saw_3, creserves.rlist_name saw_4, ttbooks.title_author saw_5, ttbooks.title saw_6, ttbooks.tt_list saw_7 FROM (SELECT "Course"."Course Code" course_code, "Course"."Course Name" course_name, "Course"."Course Year" course_year, "Course"."Course Term" course_term, "Reading List"."Reading List Name" rlist_name, "Bibliographic Details"."Title Author Combined and Normalized" title_author, "Bibliographic Details"."MMS Id" mms_id FROM "Course Reserves") creserves INNER JOIN (SELECT "Reading List"."Reading List Code" tt_list, "Bibliographic Details"."Title" title, "Bibliographic Details"."Title Author Combined and Normalized" title_author, "Bibliographic Details"."MMS Id" mms_id FROM "Course Reserves" WHERE "Reading List"."Reading List Name" IN ('Top Textbooks', 'Top Textbooks - Fall 2018')) ttbooks ON creserves.mms_id = ttbooks.mms_id