SELECT orders.title saw_0, SUM(orders.total_price) saw_2, SUM(orders.quantity) saw_3, MAX(orders.list_price) saw_4 FROM (SELECT "Bibliographic Details"."Title Author Combined and Normalized" title, "Fund Transactions"."PO Line Total Price" total_price, "Fund Transactions"."PO Line Quantity" quantity, "PO Line"."PO Line Reference" pol_ref, "PO Line"."List Price" list_price FROM "Funds Expenditure" WHERE ("Bibliographic Details"."Active Course Code" LIKE '%Top Textbooks - Spring 2020%') AND ("PO Line"."Status" <> 'CANCELLED') AND ("PO Line Type"."Format" = 'P')) orders GROUP BY orders.title