SELECT COUNT(DISTINCT courses.course_code) saw_0, COUNT(DISTINCT rlist.title) saw_1, COUNT(DISTINCT items.item_id) FROM (SELECT "Course"."Course Code" course_code, "Bibliographic Details"."MMS Id" mms_id FROM "Course Reserves" WHERE ("Course"."Course Status" = 'Active') AND ("Course"."Course Code" <> 'Top Textbooks - Spring 2019')) courses INNER JOIN (SELECT "Bibliographic Details"."MMS Id" mms_id, "Bibliographic Details"."Title Author Combined and Normalized" title FROM "Course Reserves" WHERE "Reading List"."Reading List Code" = 'Top Textbooks - Fall 2019') rlist ON courses.mms_id = rlist.mms_id INNER JOIN (SELECT "Physical Item Details"."Item Id" item_id, "Bibliographic Details"."MMS Id" mms_id FROM "Physical Items" WHERE "Temporary Location"."Temporary Location Name" IN ('Reserves (3 hours)', 'Eckles Reserves')) items ON rlist.mms_id = items.mms_id